pub enum SampleFormat {

    /// The value 0 corresponds to 0.
    I16,

    /// The value 0 corresponds to 32768.
    U16,

    /// The boundaries are (-1.0, 1.0).
    F32,
}

pub enum StreamData<'a> {
    Input {
        buffer: UnknownTypeInputBuffer,
    },
    Output {
        buffer: UnknownTypeOutputBuffer,
    },
}

pub enum UnknownTypeInputBuffer<'a> {

    /// Samples whose format is `u16`.
    U16(InputBuffer),

    /// Samples whose format is `i16`.
    I16(InputBuffer),

    /// Samples whose format is `f32`.
    F32(InputBuffer),
}

pub enum UnknownTypeOutputBuffer<'a> {

    /// Samples whose format is `u16`.
    U16(OutputBuffer),

    /// Samples whose format is `i16`.
    I16(OutputBuffer),

    /// Samples whose format is `f32`.
    F32(OutputBuffer),
}

pub enum DevicesError {

    /// See the `BackendSpecificError` docs for more information about this error variant.
    #[fail(display = "{}", err)]
    BackendSpecific {
        #[fail(cause)]
        err: BackendSpecificError,
    },
}

